import React, { useState } from "react";
import {
  Input,
  Popover,
  Radio,
  Modal,
  message,
  Button,
  Avatar,
  List,
} from "antd";
import {
  ArrowDownOutlined,
  DownOutlined,
  SettingOutlined,
} from "@ant-design/icons";
import tokenList from "../tokenList.json";

const data = [
  {
    title: "Ant Design Title 1",
  },
  {
    title: "Ant Design Title 2",
  },
  {
    title: "Ant Design Title 3",
  },
  {
    title: "Ant Design Title 4",
  },
];

function Swap() {
  const [slippage, setSlippage] = useState(0.5);
  const [tokenAmountOne, setTokenAmountOne] = useState(null);
  const [tokenAmountTwo, setTokenAmountTwo] = useState(null);
  const [tokenOne, setTokenOne] = useState(tokenList[1]);
  const [tokenTwo, setTokenTwo] = useState(tokenList[5]);

  const handleSlippageChange = (e) => {
    setSlippage(e.target.value);
  };

  const changeAmount = (e) => {
    setTokenAmountOne(e.target.value);
  };

  const switchTokens = () => {
    let tempToken = tokenOne;
    setTokenOne(tokenTwo);
    setTokenTwo(tempToken);
  };

  // ------------------ Test ------------------
  const position = "bottom";
  const align = "center";

  const [isModalOpen, setIsModalOpen] = useState(false);

  const stateModal = (value) => {
    setIsModalOpen(value);
  };
  // ------------------ Test ------------------

  const slippageOptions = [
    { label: "0.5%", value: 0.5 },
    { label: "2.5%", value: 2.5 },
    { label: "5.0%", value: 5.0 },
  ];

  const settings = (
    <>
      <div>Slippage Tolerance</div>
      <div>
        <Radio.Group
          colorText="white"
          options={slippageOptions}
          onChange={handleSlippageChange}
          value={slippage}
          optionType="button"
        />
      </div>
    </>
  );

  return (
    <>
      <>
        <Button type="primary" >
          Select a token
        </Button>
        <Modal
          title="Select a token"
          open={isModalOpen}
          footer={null}
          onCancel={() => stateModal(false)}
        >
          <>
            <List
              pagination={{ position, align }}
              dataSource={data}
              renderItem={(item, index) => (
                <List.Item>
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        src={`https://xsgames.co/randomusers/avatar.php?g=pixel&key=${index}`}
                      />
                    }
                    title={<a href="https://ant.design">{item.title}</a>}
                    description="Ant Design, a design language for background applications, is refined by Ant UED Team"
                  />
                </List.Item>
              )}
            />
          </>
        </Modal>
      </>

      <div className="tradeBox">
        <div className="tradeBoxHeader">
          <h4>Swap</h4>
          <Popover
            content={settings}
            title="Settings"
            trigger="click"
            placement="bottomRight"
          >
            <SettingOutlined className="cog" />
          </Popover>
        </div>
        <div className="inputs">
          <Input
            placeholder="0"
            value={tokenAmountOne}
            onChange={changeAmount}
          />
          <Input placeholder="0" value={tokenAmountTwo} disabled={true} />
          <div className="switchButton" onClick={switchTokens}>
            <ArrowDownOutlined className="switchArrow" />
          </div>
          <div className="assetOne" onClick={() => stateModal(true)}>
            <img src={tokenOne.img} alt="assetOneLogo" className="assetLogo" />
            {tokenOne.ticker}
            <DownOutlined />
          </div>
          <div className="assetTwo" onClick={() => stateModal(true)}>
            <img src={tokenTwo.img} alt="assetTwoLogo" className="assetLogo" />
            {tokenTwo.ticker}
            <DownOutlined />
          </div>
        </div>
      </div>
    </>
  );
}

export default Swap;
